import { interpolate, useCurrentFrame, useVideoConfig, AbsoluteFill, Sequence, spring } from 'remotion';
import React from 'react';

const COLORS = {
  bgBase: '#2D2D2D',
  cardBg: '#363636',
  accentGreen: '#7CB97A',
  accentAmber: '#D4A574',
